/**
 * @summary A convenience function for `(x, y) => x == y`
 */
export declare function equals2<T>(x: T, y: T): boolean;
/**
 * @summary A convenience function for `(x, y) => x === y`
 */
export declare function equals3<T>(x: T, y: T): boolean;
/**
 * @summary A convenience function for SameValueZero equality
 */
export declare function sameValueZero<T>(x: T, y: T): boolean;
